import "./utils/Interfaces.sol";
import "./utils/MathUtil.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title Base Reward Pool contract
/// @dev Rewards contract for Prime Pools is based on the convex contract
contract BaseRewardPool is IBaseRewardsPool {
    using SafeERC20 for IERC20;
    using MathUtil for uint256;

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event ExtraRewardsCleared();
    event ExtraRewardCleared(address extraReward);

    error Unauthorized();
    error InvalidAmount();

    uint256 public constant DURATION = 7 days;
    uint256 public constant NEW_REWARD_RATIO = 830;

    // Rewards token is Bal
    IERC20 public immutable rewardToken;
    IERC20 public immutable stakingToken;

    // Operator is Controller smart contract
    address public immutable operator;
    address public immutable rewardManager;

    uint256 public pid;
    uint256 public periodFinish;
    uint256 public rewardRate;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public queuedRewards;
    uint256 public currentRewards;
    uint256 public historicalRewards;
    uint256 private _totalSupply;

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;
    mapping(address => uint256) private _balances;

    address[] public extraRewards;

    constructor(
        uint256 pid_,
        address stakingToken_,
        address rewardToken_,
        address operator_,
        address rewardManager_
    ) {
        pid = pid_;
        stakingToken = IERC20(stakingToken_);
        rewardToken = IERC20(rewardToken_);
        operator = operator_;
        rewardManager = rewardManager_;
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = lastTimeRewardApplicable();
        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    modifier onlyAddress(address authorizedAddress) {
        if (msg.sender != authorizedAddress) {
            revert Unauthorized();
        }
        _;
    }

    /// @notice Returns total supply
    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    /// @notice Get the specified address' balance
    /// @param account The address of the token holder
    /// @return The `account`'s balance
    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    /// @notice Returns number of extra rewards
    function extraRewardsLength() external view returns (uint256) {
        return extraRewards.length;
    }

    /// @notice Adds an extra reward
    /// @dev only `rewardManager` can add extra rewards
    /// @param _reward token address of the reward
    function addExtraReward(address _reward) external onlyAddress(rewardManager) {
        require(_reward != address(0), "!reward setting");
        extraRewards.push(_reward);
    }

    /// @notice Clears extra rewards
    /// @dev Only Prime multising has the ability to do this
    /// if you want to remove only one token, use `clearExtraReward`
    function clearExtraRewards() external onlyAddress(IController(operator).owner()) {
        delete extraRewards;
        emit ExtraRewardsCleared();
    }

    /// @notice Clears extra reward by index
    /// @param index index of the extra reward to clear
    function clearExtraReward(uint256 index) external onlyAddress(IController(operator).owner()) {
        address extraReward = extraRewards[index];
        // Move the last element into the place to delete
        extraRewards[index] = extraRewards[extraRewards.length - 1];
        // Remove the last element
        extraRewards.pop();
        emit ExtraRewardCleared(extraReward);
    }

    /// @notice Returns last time reward applicable
    /// @return The lower value of current block.timestamp or last time reward applicable
    function lastTimeRewardApplicable() public view returns (uint256) {
        // solhint-disable-next-line
        return MathUtil.min(block.timestamp, periodFinish);
    }

    /// @notice Returns rewards per token staked
    /// @return The rewards per token staked
    function rewardPerToken() public view returns (uint256) {
        uint256 totalSupplyMemory = totalSupply();
        if (totalSupplyMemory == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored +
            (((lastTimeRewardApplicable() - lastUpdateTime) * rewardRate * 1e18) / totalSupplyMemory);
    }

    /// @notice Returns the `account`'s earned rewards
    /// @param account The address of the token holder
    /// @return The `account`'s earned rewards
    function earned(address account) public view returns (uint256) {
        return (balanceOf(account) * (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18 + rewards[account];
    }

    /// @notice Stakes `amount` tokens
    /// @param _amount The amount of tokens user wants to stake
    function stake(uint256 _amount) public {
        stakeFor(msg.sender, _amount);
    }

    /// @notice Stakes all BAL tokens
    function stakeAll() external {
        uint256 balance = stakingToken.balanceOf(msg.sender);
        stake(balance);
    }

    /// @notice Stakes `amount` tokens for `_for`
    /// @param _for Who are we staking for
    /// @param _amount The amount of tokens user wants to stake
    function stakeFor(address _for, uint256 _amount) public updateReward(_for) {
        if (_amount < 1) {
            revert InvalidAmount();
        }

        stakeToExtraRewards(_for, _amount);

        _totalSupply = _totalSupply + (_amount);
        // update _for balances
        _balances[_for] = _balances[_for] + (_amount);
        // take away from sender
        stakingToken.transferFrom(msg.sender, address(this), _amount);
        emit Staked(_for, _amount);
    }

    /// @notice Withdraw `amount` tokens and possibly unwrap
    /// @param _amount The amount of tokens that the user wants to withdraw
    /// @param _claim Whether or not the user wants to claim their rewards
    /// @param _unwrap Whether or not the user wants to unwrap to BLP tokens
    function withdraw(
        uint256 _amount,
        bool _claim,
        bool _unwrap
    ) public updateReward(msg.sender) {
        if (_amount < 1) {
            revert InvalidAmount();
        }

        // withdraw from linked rewards
        withdrawExtraRewards(msg.sender, _amount);

        _totalSupply = _totalSupply - (_amount);
        _balances[msg.sender] = _balances[msg.sender] - (_amount);

        if (_unwrap) {
            IController(operator).withdrawTo(pid, _amount, msg.sender);
        } else {
            // return staked tokens to sender
            stakingToken.transfer(msg.sender, _amount);
        }
        emit Withdrawn(msg.sender, _amount);

        // claim staking rewards
        if (_claim) {
            getReward(msg.sender, true);
        }
    }

    /// @notice Withdraw all tokens
    /// @param _claim Whether or not the user wants to claim their rewards
    function withdrawAll(bool _claim) external {
        withdraw(_balances[msg.sender], _claim, false);
    }

    /// @notice Withdraw all tokens and unwrap
    /// @param _claim Whether or not the user wants to claim their rewards
    function withdrawAllAndUnwrap(bool _claim) external {
        withdraw(_balances[msg.sender], _claim, true);
    }

    /// @notice Claims Rewards for `_account`
    /// @param _account The account to claim rewards for
    /// @param _claimExtras Whether or not the user wants to claim extra rewards
    function getReward(address _account, bool _claimExtras) public updateReward(_account) {
        uint256 reward = rewards[_account];
        if (reward > 0) {
            rewards[_account] = 0;
            rewardToken.safeTransfer(_account, reward);
            emit RewardPaid(_account, reward);
        }

        // also get rewards from linked rewards
        if (_claimExtras) {
            address[] memory extraRewardsMemory = extraRewards;
            for (uint256 i = 0; i < extraRewardsMemory.length; i = i.unsafeInc()) {
                IRewards(extraRewardsMemory[i]).getReward(_account);
            }
        }
    }

    /// @notice Claims Reward for signer
    function getReward() external {
        getReward(msg.sender, true);
    }

    /// @notice Donates reward token to this contract
    /// @param _amount The amount of tokens to donate
    function donate(uint256 _amount) external {
        IERC20(rewardToken).safeTransferFrom(msg.sender, address(this), _amount);
        queuedRewards = queuedRewards + _amount;
    }

    /// @notice Queue new rewards
    /// @dev Only the operator can queue new rewards
    /// @param _rewards The amount of tokens to queue
    function queueNewRewards(uint256 _rewards) external onlyAddress(operator) {
        _rewards = _rewards + queuedRewards;

        // solhint-disable-next-line
        if (block.timestamp >= periodFinish) {
            notifyRewardAmount(_rewards);
            queuedRewards = 0;
            return;
        }

        // solhint-disable-next-line
        uint256 elapsedTime = block.timestamp - (periodFinish - DURATION);
        uint256 currentAtNow = rewardRate * elapsedTime;
        uint256 queuedRatio = (currentAtNow * 1000) / _rewards;

        if (queuedRatio < NEW_REWARD_RATIO) {
            notifyRewardAmount(_rewards);
            queuedRewards = 0;
        } else {
            queuedRewards = _rewards;
        }
    }

    /// @dev Stakes `amount` tokens for address `for` to extra rewards tokens
    /// RewardManager `rewardManager` is responsible for adding reward tokens
    /// @param _for Who are we staking for
    /// @param _amount The amount of tokens user wants to stake
    function stakeToExtraRewards(address _for, uint256 _amount) internal {
        address[] memory extraRewardsMemory = extraRewards;
        for (uint256 i = 0; i < extraRewardsMemory.length; i = i.unsafeInc()) {
            IRewards(extraRewardsMemory[i]).stake(_for, _amount);
        }
    }

    /// @dev Stakes `amount` tokens for address `for` to extra rewards tokens
    /// RewardManager `rewardManager` is responsible for adding reward tokens
    /// @param _for Who are we staking for
    /// @param _amount The amount of tokens user wants to stake
    function withdrawExtraRewards(address _for, uint256 _amount) internal {
        address[] memory extraRewardsMemory = extraRewards;
        for (uint256 i = 0; i < extraRewardsMemory.length; i = i.unsafeInc()) {
            IRewards(extraRewardsMemory[i]).withdraw(_for, _amount);
        }
    }

    function notifyRewardAmount(uint256 reward) internal updateReward(address(0)) {
        historicalRewards = historicalRewards + reward;
        // solhint-disable-next-line
        if (block.timestamp >= periodFinish) {
            rewardRate = reward / DURATION;
        } else {
            // solhint-disable-next-line
            uint256 remaining = periodFinish - block.timestamp;
            uint256 leftover = remaining * rewardRate;
            reward = reward + leftover;
            rewardRate = reward / DURATION;
        }
        currentRewards = reward;
        // solhint-disable-next-line
        lastUpdateTime = block.timestamp;
        // solhint-disable-next-line
        periodFinish = block.timestamp + DURATION;
        emit RewardAdded(reward);
    }
}

contracts/factories/ProxyFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "../utils/Interfaces.sol";

/// @title ProxyFactory contract
contract ProxyFactory is IProxyFactory {
    /// @notice Creates a new contract based on the target contract address provided
    /// @param target contract address to be cloned
    /// @return result address of the new contract
    function clone(address target) external returns (address result) {
        bytes20 targetBytes = bytes20(target);
        // solhint-disable-next-line
        assembly {
            let clone := mload(0x40)
            mstore(clone, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
            mstore(add(clone, 0x14), targetBytes)
            mstore(add(clone, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
            result := create(0, clone, 0x37)
        }
    }
}

contracts/ExtraRewardStash.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./utils/Interfaces.sol";
import "./utils/MathUtil.sol";

/// @title ExtraRewardStash
contract ExtraRewardStash is IStash {
    using SafeERC20 for IERC20;
    using MathUtil for uint256;

    error Unauthorized();
    error AlreadyInitialized();

    event RewardHookSet(address newRewardHook);
    event ExtraRewardsCleared();
    event ExtraRewardCleared(address extraReward);

    uint256 private constant MAX_REWARDS = 8;
    address public immutable bal;

    uint256 public pid;
    address public operator;
    address public gauge;
    address public rewardFactory;
    address public rewardHook; // address to call for reward pulls
    bool public hasBalRewards;

    mapping(address => uint256) public historicalRewards;

    struct TokenInfo {
        address token;
        address rewardAddress;
    }

    // use mapping + array so that we dont have to loop check each time setToken is called
    mapping(address => TokenInfo) public tokenInfo;
    address[] public tokenList;

    constructor(address _bal) {
        bal = _bal;
    }

    function initialize(
        uint256 _pid,
        address _operator,
        address _gauge,
        address _rFactory
    ) external {
        if (gauge != address(0)) {
            revert AlreadyInitialized();
        }
        pid = _pid;
        operator = _operator;
        gauge = _gauge;
        rewardFactory = _rFactory;
    }

    modifier onlyAddress(address authorizedAddress) {
        if (msg.sender != authorizedAddress) {
            revert Unauthorized();
        }
        _;
    }

    /// @notice Returns the length of the tokenList
    function tokenCount() external view returns (uint256) {
        return tokenList.length;
    }

    /// @notice Claims registered reward tokens
    function claimRewards() external onlyAddress(operator) {
        // this is updateable from v2 gauges now so must check each time.
        checkForNewRewardTokens();

        if (hasBalRewards) {
            // claim rewards on gauge for staker
            // using reward_receiver so all rewards will be moved to this stash
            IController(operator).claimRewards(pid, gauge);
        }

        // hook for reward pulls
        if (rewardHook != address(0)) {
            // solhint-disable-next-line
            try IRewardHook(rewardHook).onRewardClaim() {} catch {}
        }
    }

    /// @notice Clears extra rewards
    /// @dev Only Prime multising has the ability to do this
    /// if you want to remove only one token, use `clearExtraReward`
    function clearExtraRewards() external onlyAddress(IController(operator).owner()) {
        address[] memory tokenListMemory = tokenList;

        for (uint256 i = 0; i < tokenListMemory.length; i = i.unsafeInc()) {
            delete tokenInfo[tokenListMemory[i]];
        }

        delete tokenList;
        emit ExtraRewardsCleared();
    }

    /// @notice Clears extra reward by index
    /// @param index index of the extra reward to clear
    function clearExtraReward(uint256 index) external onlyAddress(IController(operator).owner()) {
        address extraReward = tokenList[index];
        // Move the last element into the place to delete
        tokenList[index] = tokenList[tokenList.length - 1];
        // Remove the last element
        tokenList.pop();
        delete tokenInfo[extraReward];
        emit ExtraRewardCleared(extraReward);
    }

    /// @notice Checks if the gauge rewards have changed
    function checkForNewRewardTokens() internal {
        for (uint256 i = 0; i < MAX_REWARDS; i = i.unsafeInc()) {
            address token = IBalGauge(gauge).reward_tokens(i);
            if (token == address(0)) {
                break;
            }
            if (!hasBalRewards) {
                hasBalRewards = true;
            }
            setToken(token);
        }
    }

    /// @notice Registers an extra reward token to be handled
    /// @param _token The reward token address
    /// @dev Used for any new incentive that is not directly on balancer gauges
    function setExtraReward(address _token) external onlyAddress(IController(operator).owner()) {
        setToken(_token);
    }

    /// @notice Sets the reward hook address
    /// @param _hook The address of the reward hook
    function setRewardHook(address _hook) external onlyAddress(IController(operator).owner()) {
        rewardHook = _hook;
        emit RewardHookSet(_hook);
    }

    /// @notice Replaces a token on the token list
    /// @param _token The address of the token
    function setToken(address _token) internal {
        TokenInfo storage t = tokenInfo[_token];

        if (t.token == address(0)) {
            //set token address
            t.token = _token;

            //check if BAL
            if (_token != bal) {
                //create new reward contract (for NON-BAL tokens only)
                (, , , address mainRewardContract, , ) = IController(operator).poolInfo(pid);
                address rewardContract = IRewardFactory(rewardFactory).createTokenRewards(
                    _token,
                    mainRewardContract,
                    address(this)
                );

                t.rewardAddress = rewardContract;
            }
            //add token to list of known rewards
            tokenList.push(_token);
        }
    }

    /// @notice Sends all of the extra rewards to the reward contracts
    function processStash() external onlyAddress(operator) {
        uint256 tCount = tokenList.length;
        for (uint256 i = 0; i < tCount; i++) {
            TokenInfo storage t = tokenInfo[tokenList[i]];
            address token = t.token;
            if (token == address(0)) continue;

            uint256 amount = IERC20(token).balanceOf(address(this));
            if (amount > 0) {
                historicalRewards[token] = historicalRewards[token] + amount;
                if (token == bal) {
                    //if BAL, send back to booster to distribute
                    IERC20(token).safeTransfer(operator, amount);
                    continue;
                }
                //add to reward contract
                address rewards = t.rewardAddress;
                IERC20(token).safeTransfer(rewards, amount);
                IRewards(rewards).queueNewRewards(amount);
            }
        }
    }
}

contracts/Controller.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "./utils/Interfaces.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./utils/MathUtil.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title Controller contract
/// @dev Controller contract for Prime Pools is based on the convex Booster.sol contract
contract Controller is IController {
    using SafeERC20 for IERC20;
    using MathUtil for uint256;

    event OwnerChanged(address _newOwner);
    event FeeManagerChanged(address _newFeeManager);
    event PoolManagerChanged(address _newPoolManager);
    event TreasuryChanged(address _newTreasury);
    event VoteDelegateChanged(address _newVoteDelegate);
    event FeesChanged(uint256 _newPlatformFee, uint256 _newProfitFee);
    event PoolShutDown(uint256 _pid);
    event FeeTokensCleared();
    event AddedPool(
        uint256 _pid,
        address _lpToken,
        address _token,
        address _gauge,
        address _baseRewardsPool,
        address _stash
    );
    event Deposited(address _user, uint256 _pid, uint256 _amount, bool _stake);
    event Withdrawn(address _user, uint256 _pid, uint256 _amount);
    event SystemShutdown();

    error Unauthorized();
    error Shutdown();
    error PoolIsClosed();
    error InvalidParameters();
    error InvalidStash();
    error RedirectFailed();

    uint256 public constant MAX_FEES = 3000;
    uint256 public constant FEE_DENOMINATOR = 10000;
    uint256 public constant MAX_LOCK_TIME = 365 days; // 1 year is the time for the new deposided tokens to be locked until they can be withdrawn

    address public immutable bal;
    address public immutable staker;
    address public immutable feeDistro; // Balancer FeeDistributor

    uint256 public profitFees = 250; //2.5% // FEE_DENOMINATOR/100*2.5
    uint256 public platformFees = 1000; //10% //possible fee to build treasury

    address public owner;
    address public feeManager;
    address public poolManager;
    address public rewardFactory;
    address public stashFactory;
    address public tokenFactory;
    address public voteDelegate;
    address public treasury;
    address public lockRewards;

    // Balancer supports rewards in multiple fee tokens
    IERC20[] public feeTokens;
    // Fee token to VirtualBalanceReward pool mapping
    mapping(address => address) public feeTokenToPool;

    bool public isShutdown;
    bool public canClaim;

    struct PoolInfo {
        address lptoken;
        address token;
        address gauge;
        address balRewards;
        address stash;
        bool shutdown;
    }

    //index(pid) -> pool
    PoolInfo[] public poolInfo;
    mapping(address => bool) public gaugeMap;

    constructor(
        address _staker,
        address _bal,
        address _feeDistro
    ) {
        bal = _bal;
        feeDistro = _feeDistro;
        staker = _staker;
        owner = msg.sender;
        voteDelegate = msg.sender;
        feeManager = msg.sender;
        poolManager = msg.sender;
    }

    modifier onlyAddress(address authorizedAddress) {
        if (msg.sender != authorizedAddress) {
            revert Unauthorized();
        }
        _;
    }

    modifier isNotShutDown() {
        if (isShutdown) {
            revert Shutdown();
        }
        _;
    }

    /// SETTER SECTION ///

    /// @notice sets the owner variable
    /// @param _owner The address of the owner of the contract
    function setOwner(address _owner) external onlyAddress(owner) {
        owner = _owner;
        emit OwnerChanged(_owner);
    }

    /// @notice sets the feeManager variable
    /// @param _feeM The address of the fee manager
    function setFeeManager(address _feeM) external onlyAddress(feeManager) {
        feeManager = _feeM;
        emit FeeManagerChanged(_feeM);
    }

    /// @notice sets the poolManager variable
    /// @param _poolM The address of the pool manager
    function setPoolManager(address _poolM) external onlyAddress(poolManager) {
        poolManager = _poolM;
        emit PoolManagerChanged(_poolM);
    }

    /// @notice sets the reward, token, and stash factory addresses
    /// @param _rfactory The address of the reward factory
    /// @param _sfactory The address of the stash factory
    /// @param _tfactory The address of the token factory
    function setFactories(
        address _rfactory,
        address _sfactory,
        address _tfactory
    ) external onlyAddress(owner) {
        //reward factory only allow this to be called once even if owner
        //removes ability to inject malicious staking contracts
        //token factory can also be immutable
        if (rewardFactory == address(0)) {
            rewardFactory = _rfactory;
            tokenFactory = _tfactory;
        }

        //stash factory should be considered more safe to change
        //updating may be required to handle new types of gauges
        stashFactory = _sfactory;
    }

    /// @notice sets the voteDelegate variable
    /// @param _voteDelegate The address of whom votes will be delegated to
    function setVoteDelegate(address _voteDelegate) external onlyAddress(voteDelegate) {
        voteDelegate = _voteDelegate;
        emit VoteDelegateChanged(_voteDelegate);
    }

    /// @notice sets the lockRewards variable
    /// @param _rewards The address of the rewards contract
    function setRewardContracts(address _rewards) external onlyAddress(owner) {
        if (lockRewards == address(0)) {
            lockRewards = _rewards;
        }
    }

    /// @notice sets the address of the feeToken
    /// @param _feeToken feeToken
    function addFeeToken(IERC20 _feeToken) external onlyAddress(feeManager) {
        feeTokens.push(_feeToken);
        // If fee token is BAL forward rewards to BaseRewardPool
        if (address(_feeToken) == bal) {
            feeTokenToPool[address(_feeToken)] = lockRewards;
            return;
        }
        // Create VirtualBalanceRewardPool and forward rewards there for other tokens
        address virtualBalanceRewardPool = IRewardFactory(rewardFactory).createTokenRewards(
            address(_feeToken),
            lockRewards,
            address(this)
        );
        feeTokenToPool[address(_feeToken)] = virtualBalanceRewardPool;
    }

    /// @notice Clears fee tokens
    function clearFeeTokens() external onlyAddress(feeManager) {
        delete feeTokens;
        emit FeeTokensCleared();
    }

    /// @notice sets the lock, staker, caller, platform fees and profit fees
    /// @param _profitFee The amount to set for the profit fees
    /// @param _platformFee The amount to set for the platform fees
    function setFees(uint256 _platformFee, uint256 _profitFee) external onlyAddress(feeManager) {
        uint256 total = _profitFee + _platformFee;
        if (total > MAX_FEES) {
            revert InvalidParameters();
        }

        //values must be within certain ranges
        if (
            _platformFee >= 500 && //5%
            _platformFee <= 2000 && //20%
            _profitFee >= 100 && //1%
            _profitFee <= 1000 //10%
        ) {
            platformFees = _platformFee;
            profitFees = _profitFee;
            emit FeesChanged(_platformFee, _profitFee);
        }
    }

    /// @notice sets the contracts treasury variables
    /// @param _treasury The address of the treasury contract
    function setTreasury(address _treasury) external onlyAddress(feeManager) {
        treasury = _treasury;
        emit TreasuryChanged(_treasury);
    }

    /// END SETTER SECTION ///

    /// @inheritdoc IController
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function feeTokensLength() external view returns (uint256) {
        return feeTokens.length;
    }

    /// @notice creates a new pool
    /// @param _lptoken The address of the lp token
    /// @param _gauge The address of the gauge controller
    function addPool(address _lptoken, address _gauge) external onlyAddress(poolManager) isNotShutDown {
        if (_gauge == address(0) || _lptoken == address(0) || gaugeMap[_gauge]) {
            revert InvalidParameters();
        }
        //the next pool's pid
        uint256 pid = poolInfo.length;
        //create a tokenized deposit
        address token = ITokenFactory(tokenFactory).createDepositToken(_lptoken);
        //create a reward contract for bal rewards
        address newRewardPool = IRewardFactory(rewardFactory).createBalRewards(pid, token);
        //create a stash to handle extra incentives
        address stash = IStashFactory(stashFactory).createStash(pid, _gauge);

        if (stash == address(0)) {
            revert InvalidStash();
        }

        //add the new pool
        poolInfo.push(
            PoolInfo({
                lptoken: _lptoken,
                token: token,
                gauge: _gauge,
                balRewards: newRewardPool,
                stash: stash,
                shutdown: false
            })
        );
        gaugeMap[_gauge] = true;
        // give stashes access to RewardFactory and VoterProxy
        // VoterProxy so that it can grab the incentive tokens off the contract after claiming rewards
        // RewardFactory so that stashes can make new extra reward contracts if a new incentive is added to the gauge
        poolInfo[pid].stash = stash;
        IRewardFactory(rewardFactory).grantRewardStashAccess(stash);
        redirectGaugeRewards(stash, _gauge);
        emit AddedPool(pid, _lptoken, token, _gauge, newRewardPool, stash);
    }

    /// @notice Shuts down multiple pools
    /// @dev Claims rewards for that pool before shutting it down
    /// @param _startPoolIdx Start pool index
    /// @param _endPoolIdx End pool index (excluded)
    function bulkPoolShutdown(uint256 _startPoolIdx, uint256 _endPoolIdx) external onlyAddress(poolManager) {
        for (uint256 i = _startPoolIdx; i < _endPoolIdx; i = i.unsafeInc()) {
            PoolInfo storage pool = poolInfo[i];

            if (pool.shutdown) {
                continue;
            }

            _earmarkRewards(i);

            //withdraw from gauge
            // solhint-disable-next-line
            try IVoterProxy(staker).withdrawAll(pool.lptoken, pool.gauge) {
                // solhint-disable-next-line
            } catch {}

            pool.shutdown = true;
            gaugeMap[pool.gauge] = false;
            emit PoolShutDown(i);
        }
    }

    /// @notice shuts down all pools
    /// @dev This shuts down the contract
    function shutdownSystem() external onlyAddress(owner) {
        isShutdown = true;
        emit SystemShutdown();
    }

    /// @inheritdoc IController
    function deposit(
        uint256 _pid,
        uint256 _amount,
        bool _stake
    ) public isNotShutDown {
        PoolInfo storage pool = poolInfo[_pid];
        if (pool.shutdown) {
            revert PoolIsClosed();
        }
        //send to proxy to stake
        address lptoken = pool.lptoken;
        IERC20(lptoken).transferFrom(msg.sender, staker, _amount);

        //stake
        address gauge = pool.gauge;
        IVoterProxy(staker).deposit(lptoken, gauge); // VoterProxy

        address token = pool.token; //D2DPool token
        if (_stake) {
            //mint here and send to rewards on user behalf
            ITokenMinter(token).mint(address(this), _amount);
            address rewardContract = pool.balRewards;
            IERC20(token).approve(rewardContract, _amount);
            IRewards(rewardContract).stakeFor(msg.sender, _amount);
        } else {
            //add user balance directly
            ITokenMinter(token).mint(msg.sender, _amount);
        }

        emit Deposited(msg.sender, _pid, _amount, _stake);
    }

    /// @inheritdoc IController
    function depositAll(uint256 _pid, bool _stake) external {
        address lptoken = poolInfo[_pid].lptoken;
        uint256 balance = IERC20(lptoken).balanceOf(msg.sender);
        deposit(_pid, balance, _stake);
    }

    /// @notice internal function that withdraws lp tokens from the pool
    /// @param _pid The pool id to withdraw the tokens from
    /// @param _amount amount of LP tokens to withdraw
    /// @param _from address of where the lp tokens will be withdrawn from
    /// @param _to address of where the lp tokens will be sent to
    function _withdraw(
        uint256 _pid,
        uint256 _amount,
        address _from,
        address _to
    ) internal {
        PoolInfo storage pool = poolInfo[_pid];
        address lptoken = pool.lptoken;
        address gauge = pool.gauge;

        //remove lp balance
        address token = pool.token;
        ITokenMinter(token).burn(_from, _amount);

        //pull from gauge if not shutdown
        // if shutdown tokens will be in this contract
        if (!pool.shutdown) {
            IVoterProxy(staker).withdraw(lptoken, gauge, _amount);
        }
        //return lp tokens
        IERC20(lptoken).transfer(_to, _amount);

        emit Withdrawn(_to, _pid, _amount);
    }

    /// @inheritdoc IController
    function withdraw(uint256 _pid, uint256 _amount) public {
        _withdraw(_pid, _amount, msg.sender, msg.sender);
    }

    /// @inheritdoc IController
    function withdrawAll(uint256 _pid) public {
        address token = poolInfo[_pid].token;
        uint256 userBal = IERC20(token).balanceOf(msg.sender);
        withdraw(_pid, userBal);
    }

    /// @inheritdoc IController
    function withdrawTo(
        uint256 _pid,
        uint256 _amount,
        address _to
    ) external {
        address rewardContract = poolInfo[_pid].balRewards;
        if (msg.sender != rewardContract) {
            revert Unauthorized();
        }
        _withdraw(_pid, _amount, msg.sender, _to);
    }

    /// @inheritdoc IController
    function withdrawUnlockedWethBal() external onlyAddress(owner) {
        canClaim = true;
        IVoterProxy(staker).withdrawWethBal(address(this));
    }

    /// @inheritdoc IController
    function redeemWethBal() external {
        require(canClaim);
        IBalDepositor balDepositor = IBalDepositor(IVoterProxy(staker).depositor());
        uint256 balance = IERC20(balDepositor.d2dBal()).balanceOf(msg.sender);
        balDepositor.burnD2DBal(msg.sender, balance);
        IERC20(balDepositor.wethBal()).safeTransfer(msg.sender, balance);
    }

    /// @notice Delegates voting power from VoterProxy
    /// @param _delegateTo to whom we delegate voting power
    function delegateVotingPower(address _delegateTo) external onlyAddress(owner) {
        IVoterProxy(staker).delegateVotingPower(_delegateTo);
    }

    /// @notice Clears delegation of voting power from EOA for VoterProxy
    function clearDelegation() external onlyAddress(owner) {
        IVoterProxy(staker).clearDelegate();
    }

    /// @notice Votes for multiple gauges
    /// @param _gauges array of gauge addresses
    /// @param _weights array of vote weights
    function voteGaugeWeight(address[] calldata _gauges, uint256[] calldata _weights)
        external
        onlyAddress(voteDelegate)
    {
        IVoterProxy(staker).voteMultipleGauges(_gauges, _weights);
    }

    /// @notice claims rewards from a specific pool
    /// @param _pid the id of the pool
    /// @param _gauge address of the gauge
    function claimRewards(uint256 _pid, address _gauge) external {
        address stash = poolInfo[_pid].stash;
        if (msg.sender != stash) {
            revert Unauthorized();
        }
        IVoterProxy(staker).claimRewards(_gauge);
    }

    /// @notice internal function that claims rewards from a pool and disperses them to the rewards contract
    /// @param _pid the id of the pool where lp tokens are held
    function _earmarkRewards(uint256 _pid) internal {
        PoolInfo storage pool = poolInfo[_pid];
        if (pool.shutdown) {
            revert PoolIsClosed();
        }
        address gauge = pool.gauge;

        //claim bal
        IVoterProxy(staker).claimBal(gauge);

        //check if there are extra rewards
        address stash = pool.stash;
        if (stash != address(0)) {
            //claim extra rewards
            IStash(stash).claimRewards();
            //process extra rewards
            IStash(stash).processStash();
        }

        //bal balance
        uint256 balBal = IERC20(bal).balanceOf(address(this));

        if (balBal > 0) {
            //Profit fees are taken on the rewards together with platform fees.
            uint256 _profit = (balBal * profitFees) / FEE_DENOMINATOR;
            //profit fees are distributed to the gnosisSafe, which owned by Prime; which is here feeManager
            IERC20(bal).transfer(feeManager, _profit);

            //send treasury
            if (treasury != address(0) && treasury != address(this) && platformFees > 0) {
                //only subtract after address condition check
                uint256 _platform = (balBal * platformFees) / FEE_DENOMINATOR;
                balBal = balBal - _platform;
                IERC20(bal).transfer(treasury, _platform);
            }
            balBal = balBal - _profit;

            //send bal to lp provider reward contract
            address rewardContract = pool.balRewards;
            IERC20(bal).transfer(rewardContract, balBal);
            IRewards(rewardContract).queueNewRewards(balBal);
        }
    }

    /// @inheritdoc IController
    function earmarkRewards(uint256 _pid) external {
        _earmarkRewards(_pid);
    }

    /// @inheritdoc IController
    function earmarkFees() external {
        IERC20[] memory feeTokensMemory = feeTokens;
        // Claim fee rewards from fee distro
        IVoterProxy(staker).claimFees(feeDistro, feeTokensMemory);

        // VoterProxy transfers rewards to this contract, and we need to distribute them to
        // VirtualBalanceRewards contracts
        for (uint256 i = 0; i < feeTokensMemory.length; i = i.unsafeInc()) {
            IERC20 feeToken = feeTokensMemory[i];
            uint256 balance = feeToken.balanceOf(address(this));
            if (balance != 0) {
                feeToken.safeTransfer(feeTokenToPool[address(feeToken)], balance);
                IRewards(feeTokenToPool[address(feeToken)]).queueNewRewards(balance);
            }
        }
    }

    /// @notice redirects rewards from gauge to rewards contract
    /// @param _stash stash address
    /// @param _gauge gauge address
    function redirectGaugeRewards(address _stash, address _gauge) private {
        bytes memory data = abi.encodeWithSelector(bytes4(keccak256("set_rewards_receiver(address)")), _stash);
        (bool success, ) = IVoterProxy(staker).execute(_gauge, uint256(0), data);
        if (!success) {
            revert RedirectFailed();
        }
    }
}

